import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
// import App from './App';
import $ from 'jquery';
import InputCpu from './components/InputCpu';
import InputMotherboard from './components/InputMotherboard';
import InputGPU from './components/InputGPU';
import InputStorage from './components/InputStorage';
import InputCasing from './components/InputCasing';
import Login from './components/Login';
import InputRAM from './components/InputRAM';
import InputPSU from './components/InputPSU';
import InputFan from './components/InputFan';
import RakitPC from './components/RakitPC';
import LowTier from './components/LowTier';
import MidTier from './components/MidTier';
import HighTier from './components/HighTier';

$('#ambilCPU').on('click', function (event) {
  ReactDOM.render(
    <React.StrictMode>
      <div className='container'>
        <InputCpu />
      </div>
    </React.StrictMode>,
    document.getElementById('root')
  );
});

$('#ambilMotherboard').on('click', function (event) {
  ReactDOM.render(
    <React.StrictMode>
      <div className='container'>
        <InputMotherboard />
      </div>
    </React.StrictMode>,
    document.getElementById('root')
  );
});

$('#ambilVGA').on('click', function (event) {
  ReactDOM.render(
    <React.StrictMode>
      <div className='container'>
        <InputGPU />
      </div>
    </React.StrictMode>,
    document.getElementById('root')
  );
});

$('#ambilSSD').on('click', function (event) {
  ReactDOM.render(
    <React.StrictMode>
      <div className='container'>
        <InputStorage />
      </div>
    </React.StrictMode>,
    document.getElementById('root')
  );
});

$('#ambilFan').on('click', function (event) {
  ReactDOM.render(
    <React.StrictMode>
      <div className='container'>
        <InputFan />
      </div>
    </React.StrictMode>,
    document.getElementById('root')
  );
});

$('#ambilCase').on('click', function (event) {
  ReactDOM.render(
    <React.StrictMode>
      <div className='container'>
        <InputCasing />
      </div>
    </React.StrictMode>,
    document.getElementById('root')
  );
});

$('#ambilRAM').on('click', function (event) {
  ReactDOM.render(
    <React.StrictMode>
      <div className='container'>
        <InputRAM />
      </div>
    </React.StrictMode>,
    document.getElementById('root')
  );
});

$('#ambilPSU').on('click', function (event) {
  ReactDOM.render(
    <React.StrictMode>
      <div className='container'>
        <InputPSU />
      </div>
    </React.StrictMode>,
    document.getElementById('root')
  );
});

$('#loginDong').on('click', function (event) {
  ReactDOM.render(
    <React.StrictMode>
      <div className='container'>
        <Login />
      </div>
    </React.StrictMode>,
    document.getElementById('root')
  );
});


$('#kuyRakit').on('click', function (event) {
  ResetRakitPC();
  ReactDOM.render(
    <React.StrictMode>
      <div className='container'>
        <Login />
      </div>
    </React.StrictMode>,
    document.getElementById('rakit')
  );
});

$('#lowTier').on('click', function (event) {
  ReactDOM.render(
    <React.StrictMode>
      <div className='container'>
        <LowTier />
      </div>
    </React.StrictMode>,
    document.getElementById('root2')
  );
});

$('#midTier').on('click', function (event) {
  ReactDOM.render(
    <React.StrictMode>
      <div className='container'>
        <MidTier />
      </div>
    </React.StrictMode>,
    document.getElementById('root2')
  );
});

$('#highTier').on('click', function (event) {
  ReactDOM.render(
    <React.StrictMode>
      <div className='container'>
        <HighTier />
      </div>
    </React.StrictMode>,
    document.getElementById('root2')
  );
});

const ResetRakitPC = async () => {
  try {
    const response = await fetch(
      `http://localhost:3080/startRakit`,
      {
        method: "POST",
        headers: { "Content-Type": "application/json" },
      }
    );
  } catch (err) {
    console.error(err.message);
  }
};

// Set a variable for our button element.
const scrollToTopButton = document.getElementById('js-top');

// Let's set up a function that shows our scroll-to-top button if we scroll beyond the height of the initial window.
const scrollFunc = () => {
  // Get the current scroll value
  let y = window.scrollY;

  // If the scroll value is greater than the window height, let's add a class to the scroll-to-top button to show it!
  if (y > 0) {
    scrollToTopButton.className = "top-header";
  } else {
    scrollToTopButton.className = "top-header";
  }
};

window.addEventListener("scroll", scrollFunc);

const scrollToTop = () => {
  // Let's set a variable for the number of pixels we are from the top of the document.
  const c = document.documentElement.scrollTop || document.body.scrollTop;

  // If that number is greater than 0, we'll scroll back to 0, or the top of the document.
  // We'll also animate that scroll with requestAnimationFrame:
  // https://developer.mozilla.org/en-US/docs/Web/API/window/requestAnimationFrame
  if (c > 0) {
    window.requestAnimationFrame(scrollToTop);
    // ScrollTo takes an x and a y coordinate.
    // Increase the '10' value to get a smoother/slower scroll!
    window.scrollTo(0, c - c / 10);
  }
};

// When the button is clicked, run our ScrolltoTop function above!
scrollToTopButton.onclick = function (e) {
  e.preventDefault();
  scrollToTop();
}
