import React, { Fragment, useEffect, useState } from "react";

const LowTier = () => {
    const [todos, setTodos] = useState([]);

    const getHardware = async () => {
        try {
            const response = await fetch("http://localhost:3080/see/motherboard");
            const jsonData = await response.json();

            setTodos(jsonData);
        } catch (err) {
            console.error(err.message);
        }
    };

    useEffect(() => {
        getHardware();
    }, []);

    console.log(todos);

    return (
        <Fragment>
            <div class="d-flex justify-content-center">
                <h2>Deskripsi Komponen</h2>
            </div>
            <div class="row justify-content-center">
                <div class="col-lg-4 col-md-6 col-sm-6 mb-5">
                    <div class="single-project">
                        <img src="images/rekomendasi/lowtier.jpg" class="img-fluid">
                        </img>
                        <div class="project-content">
                            <h4>Low Tier</h4>
                            <p>AMD Ryzen 5 3600<br></br>
                                ZOTAC Mini GeForce GTX 1050 TI 4GB<br></br>
                                MSI B450 Tomahawk MAX<br></br>
                                Teamgroup T-CREATE Classic 16GB<br></br>
                                Seagate Barracuda 2TB</p>
                        </div>

                    </div>
                </div>
            </div>

        </Fragment>
    );
};

export default LowTier;
