import { get } from "jquery";
import React, { Fragment, useEffect, useState } from "react";
import ShowLowTier from "./ShowLowTier";

const LowTier = () => {
    const [todos, setTodos] = useState([]);
    const [cpu, setTodos2] = useState([]);
    //const []

    const getHardware = async () => {
        try {
            const response = await fetch(`http://localhost:3080/recommend/1`);

            const jsonData = await response.json();

            setTodos(jsonData);

        } catch (err) {
            console.error(err.message);
        }
    };

    function timeout(delay: number) {
        return new Promise(res => setTimeout(res, delay));
    }

    const getData = async () => {
        try {
            const response = await fetch(`http://localhost:3080/see/cpu/${todos.id_cpu}`);
            const jsonData = await response.json();

            setTodos2(jsonData);
        } catch (err) {
            console.error(err.message);
        }
    };

    useEffect(() => {
        async function fetchAPI() {
            getHardware();
            await timeout(2000);
            console.log(todos.id_cpu);
            await timeout(2000);
            await timeout(2000);
            getData();
        }
        fetchAPI();

    }, []);

    //console.log(cpu);
    //console.log(cpuDesc);

    return (
        <Fragment>

            <div class="row py-0">
                <div class="col-lg-6">
                    <div class="single-project">
                        <img src="images/rekomendasi/lowtier.jpg" alt="" class="img-fluid">
                        </img>
                    </div>
                </div>

                <div class="col-lg-5">


                    <div>
                        <h2>Low Tier</h2>

                        <ShowLowTier todo={todos} />

                        <h3>CPU Detail: {cpu.name} , Clock: {cpu.core} </h3>


                    </div>


                </div>
            </div>
            <pre class="tab"> </pre>
            <pre class="tab"> </pre>
        </Fragment>
    );
};

export default LowTier;
