import React, { Fragment, useEffect, useState } from "react";

const InputCpu = () => {
    const [todos, setTodos] = useState([]);

    //delete todo function

    const getHardware = async () => {
        try {
            const response = await fetch("http://localhost:3080/see");
            const jsonData = await response.json();

            setTodos(jsonData);
        } catch (err) {
            console.error(err.message);
        }
    };

    useEffect(() => {
        getHardware();
    }, []);

    console.log(todos);

    return (
        <Fragment>
            {" "}
            <h2>CPU Lists</h2>
            <table class="table mt-5 text-center">
                <thead>
                    <tr>
                        <th>id</th>
                        <th>name</th>
                        <th>core</th>
                        <th>clock</th>
                        <th>integrated</th>
                        <th>price</th>
                    </tr>
                </thead>
                <tbody>
                    {/* <tr>
                        <td>John</td>
                        <td>Doe</td>
                        <td>john@example.com</td>
                    </tr> */}
                    {todos.map(todo => (
                        <tr>
                            <td>{todo.id_cpu}</td>
                            <td>{todo.name}</td>
                            <td>{todo.core}</td>
                            <td>{todo.clock}</td>
                            <td>{todo.integrated}</td>
                            <td>{todo.price}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </Fragment>
    );
};

export default InputCpu;
