import React, { Fragment, useEffect, useState } from "react";

const inputCasing = () => {
    const [todos, setTodos] = useState([]);

    //delete todo function

    const getHardware = async () => {
        try {
            const response = await fetch("http://localhost:3080/see");
            const jsonData = await response.json();

            setTodos(jsonData);
        } catch (err) {
            console.error(err.message);
        }
    };

    useEffect(() => {
        getHardware();
    }, []);

    console.log(todos);

    return (
        <Fragment>
            {" "}
            <h2>Motherboard Lists</h2>
            <table class="table mt-5 text-center">
                <thead>
                    <tr>
                        <th>No</th>
                        <th>Name</th>
                        <th>Form Factor</th>
                        <th>Brand</th>
                        <th>Price</th>
                    </tr>
                </thead>
                <tbody>
                    {/* <tr>
                        <td>John</td>
                        <td>Doe</td>
                        <td>john@example.com</td>
                    </tr> */}
                    {todos.map(todo => (
                        <tr>
                            <td>{todo.id_case}</td>
                            <td>{todo.name}</td>
                            <td>{todo.ff_tipe}</td>
                            <td>{todo.brand}</td>
                            <td>{todo.price}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </Fragment>
    );
};

export default inputCasing;
