import React, { Fragment, useEffect, useState } from "react";

const Login = () => {
    const [todos, setTodos] = useState([]);

    //delete function

    const getHardware = async () => {
        try {
            const response = await fetch("http://localhost:3080/see/psu");
            const jsonData = await response.json();

            setTodos(jsonData);
        } catch (err) {
            console.error(err.message);
        }
    };

    const post = async () => {
        try {
            const response = await fetch("http://localhost:3080/see/psu");
            const jsonData = await response.json();

            setTodos(jsonData);
        } catch (err) {
            console.error(err.message);
        }
    };


    useEffect(() => {
        getHardware();
    }, []);

    return (
        <Fragment>
            <button type="button" class="btn btn-outline-warning btn-sm" data-toggle="modal" data-target="#myModal">
                Login
            </button>
            <div class="modal" id="myModal">
                <div class="modal-dialog">
                    <div class="modal-content">

                        <div class="modal-header">
                            <h4 class="modal-title">Login</h4>
                            <button type="button" class="close" data-dismiss="modal">&times;</button>
                        </div>

                        <div class="modal-body">
                            <div class="form-label-group">
                                <input type="text" name="username" id="inputEmail" class="form-control" placeholder="Username" required
                                    autofocus></input>
                                <label for="inputEmail">Username</label>
                            </div>

                            <div class="form-label-group">
                                <input type="password" name="password" id="inputPassword" class="form-control" placeholder="Password"
                                    required></input>
                                <label for="inputPassword">Password</label>
                            </div>
                        </div>


                        <div class="modal-footer">
                            <button type="button" class="btn btn-success" data-dismiss="modal">Login</button>
                            <button type="button" class="btn btn-danger" data-dismiss="modal">Close</button>
                        </div>

                    </div>
                </div>
            </div>
        </Fragment >
    );
};

export default Login;
