import React, { Fragment, useEffect, useState } from "react";

const LowTier = () => {
    const [todos, setTodos] = useState([]);
    const [cpu, setTodos2] = useState([]);
    //const []


    function timeout(delay: number) {
        return new Promise(res => setTimeout(res, delay));
    }

    const getHardware = async () => {
        try {
            const response = await fetch(`http://localhost:3080/recommend/1`);

            const jsonData = await response.json();

            setTodos(jsonData);
            await timeout(3000); //for 1 sec delay
        } catch (err) {
            console.error(err.message);
        }
    };

    var cpuDesc = todos.id_cpu;
    const getData = async () => {
        try {
            const response = await fetch(`http://localhost:3080/see/cpu/${todos.id_cpu}`);
            const jsonData = await response.json();

            setTodos2(jsonData);
        } catch (err) {
            console.error(err.message);
        }
    };

    useEffect(() => {
        getHardware();
        getData();
    }, []);

    console.log(todos);
    console.log(cpuDesc);
    console.log(cpu);

    return (
        <Fragment>

            <div class="row py-0">
                <div class="col-lg-6">
                    <div class="single-project">
                        <img src="images/rekomendasi/lowtier.jpg" alt="" class="img-fluid">
                        </img>
                    </div>
                </div>

                <div class="col-lg-5">


                    <div>
                        <h2>Low Tier</h2>
                        <h3>CPU Detail: {cpu.name} , Clock: {cpu.core} </h3>


                    </div>


                </div>
            </div>
            <pre class="tab"> </pre>
            <pre class="tab"> </pre>
        </Fragment>
    );
};

export default LowTier;
