import React, { Fragment, useEffect, useState } from "react";

const LowTier = () => {
    const [todos, setTodos] = useState([]);
    const [desc, setTodos2] = useState([]);

    const getHardware = async () => {
        try {
            const response = await fetch(`http://localhost:3080/recommend/1`);
            const jsonData = await response.json();

            setTodos(jsonData);
        } catch (err) {
            console.error(err.message);
        }
    };
    var cpuDesc = todos.id_cpu;
    const getData = async () => {
        try {
            const response = await fetch(`http://localhost:3080/see/cpu/${cpuDesc}`);
            const jsonData = await response.json();

            setTodos2(jsonData);
        } catch (err) {
            console.error(err.message);
        }
    };

    useEffect(() => {
        getHardware();
        //getData();
    }, []);

    console.log(todos);

    return (
        <Fragment>

            <h2>Casing Lists</h2>
            <table class="table mt-5 text-center">
                <thead>
                    <tr>
                        <th>No</th>
                        <th>Name</th>
                        <th>Form Factor</th>
                        <th>Brand</th>
                        <th>Price</th>
                        <th>Edit</th>
                        <th>Delete</th>
                    </tr>
                </thead>
                <tbody>
                    {todos.map(todo => (
                        <tr>
                            <td>{todo.id_case}</td>
                            <td>{todo.name}</td>
                            <td>{todo.ff_tipe}</td>
                            <td>{todo.brand}</td>

                        </tr>
                    ))}
                </tbody>
            </table>
        </Fragment>
    );
};

export default LowTier;
